import React, { useEffect, useState } from "react";

function Example() {
    const [color, setColor] = useState ('blue');
  
    useEffect(() => {
      // Atualiza a cor da DIV pai e da DIV filha
      document.getElementById('parent').style.color = color;
      document.getElementById('child').style.color = color;
    }, [color]);}
  

function Testing (){
    return(
        <div id="parent">
        <div id="child">Text</div>
        <button onClick={() => S('red')}>Mudar cor</button>
      </div>
    )
}

export default Testing
