import React from "react";
import './header.css';
import Menuresp from './iconemenu.png'

function Header (){

    const [active, setActive] = useState("nav__menu");
    const [icon, setIcon] = useState("nav__toggler");
    const navToggle = () => {
      if (active === "nav__menu") {
        setActive("nav__menu nav__active");
      } else setActive("nav__menu");

      // Icone
      if (icon === "nav__toggler") {
        setIcon("nav__toggler toggle");
      } else setIcon("nav__toggler");
    };


    return(
        <div>
           <header>
            <nav className="nav">
                <div className="name">CARLOS HENRIQUE PIZZI</div>
                    <ul className={active} id="menu-h">

                    <a className="nav__link" href="#"><li className="nav__item">HOME</li></a>

                    <a className="nav__link" href="#02"><li className="nav__item">PROJETOS</li></a>

                    <a  className="nav__link"href="#01"><li className="nav__item">SOBRE MIM</li></a>

                    <a className="nav__link" href="#03"><li className="nav__item">CONTATOS</li></a>

                    {/* <div className="iconemobile">
                       <button className="butonmenuresp" onClick={handleClick}><img id="menuresp" src={Menuresp} /></button>
                    </div> */}
                    </ul>
                    <div onClick={navToggle} className={icon}>
                        <div className="line1"></div>
                        <div className="line2"></div>
                        <div className="line3"></div>
      </div>

            </nav>

            <div className="mobilemenu" ref={menuMobile} style={{ display: open ? 'block' : 'none' }}>  

                <ul id="menu-h" >

                        <a href="#"><li >HOME</li></a>

                        <a href="#02"><li >PROJETOS</li></a>

                        <a href="#01"><li >SOBRE MIM</li></a>

                        <a href="#03"><li >CONTATOS</li></a>

                </ul>
            </div>

           </header>

        </div>
    )
}

export default Header
