import React from "react";
import './header.css';
import Menuresp from './iconemenu.png'

function Header (){

    function menuShow(){
        let menuMobile = document.querySelector('.mobilemenu')
        if (menuMobile.classList.contains('open')){
            menuMobile.classList.remove('open')

        }else{
            menuMobile.classList.add('open')
        }
    }

    return(
        <div>
           <header>
            <nav className="nav-bar">
                <div className="name">CARLOS HENRIQUE PIZZI</div>

                    <ul id="menu-h">

                    <a href="#"><li className="nav-item">HOME</li></a>

                    <a href="#02"><li className="nav-item">PROJETOS</li></a>

                    <a href="#01"><li className="nav-item">SOBRE MIM</li></a>

                    <a href="#03"><li className="nav-item">CONTATOS</li></a>
                    <div className="mobile-menu-icon">
                       <button className="butonmenuresp" onClick={menuShow}><img className="icon" src={Menuresp} /></button>
                    </div>
                    </ul>

            </nav>

            <div className="mobile-menu">
                <div className="name">CARLOS HENRIQUE PIZZI</div>
                <ul id="menu-h">

                        <a href="#"><li className="nav-item">HOME</li></a>

                        <a href="#02"><li className="navitem">PROJETOS</li></a>

                        <a href="#01"><li className="nav-item">SOBRE MIM</li></a>

                        <a href="#03"><li className="nav-item">CONTATOS</li></a>

                </ul>
            </div>

           </header>

        </div>
    )
}

export default Header
