import React from "react";
import './header.css';
import Menuresp from './iconemenu.png'
import { useState, useEffect } from "react";
function Header (){
    const [width, setWidth] = useState(window.innerWidth);
    const [showHeader, setShowHeader] = useState(width < 768);

    useEffect(() => {
      function handleResize() {
        setWidth(window.innerWidth);
        setShowHeader(width < 768);
      }
      window.addEventListener('resize', handleResize);
      return () => {
        window.removeEventListener('resize', handleResize);
      };
    }, [width]);

    const [open, setOpen] = React.useState(false);
    const menuMobile = React.useRef(null);

    const handleClick = () => {
      setOpen(!open);
    };


    return(
        <div>
           <header>
            <nav className="navbar">
                <div className="name">CARLOS HENRIQUE PIZZI</div>
                    <ul id="menu-h">

                    <a href="#"><li className="navitem">HOME</li></a>

                    <a href="#02"><li className="navitem">PROJETOS</li></a>

                    <a href="#01"><li className="navitem">SOBRE MIM</li></a>

                    <a href="#03"><li className="navitem">CONTATOS</li></a>
                    <div className="iconemobile">
                       <button className="butonmenuresp" onClick={handleClick}><img id="menuresp" src={Menuresp} /></button>
                    </div>
                    </ul>

            </nav>

            <div className="mobilemenu" ref={menuMobile} style={{ display: open ? 'block' : 'none' }}>  

                <ul id="menu-h" >

                        <a href="#"><li >HOME</li></a>

                        <a href="#02"><li >PROJETOS</li></a>

                        <a href="#01"><li >SOBRE MIM</li></a>

                        <a href="#03"><li >CONTATOS</li></a>

                </ul>
            </div>

           </header>

        </div>
    )
}

export default Header
